// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {ITasks} from "../../lib/openrd-foundry/src/ITasks.sol";
import {IDAOManager, IDAO} from "../../lib/trustless-management/src/IDAOManager.sol";

interface ITaskDisputes {
    error Underpaying();
    error TransferToDAOFailed();

    event DisputeCreated(IDAO indexed dao, DisputeInfo dispute, address governancePlugin, uint256 proposalId);

    /// @notice A container for all info related to a certain DAO.
    /// @param disputeCost How much native currency should be paid to be allowed to create a dispute in this DAO.
    /// @param governancePlugin The contract where to create the proposals. (Currently this only supports MajorityVoting plugins from Aragon)
    /// @param manager Mangement solution used by the DAO.
    /// @param role Role to use to be allowed to create proposals in the DAO.
    struct DaoInfo {
        uint256 disputeCost;
        address governancePlugin;
        IDAOManager manager;
        uint256 role;
    }

    /// @notice A container for all info needed to make an task dispute.
    /// @param tasks The contract containing the task.
    /// @param taskId The task wanting to complete by dispute.
    /// @param partialNativeReward Complete with how much of the native reward.
    /// @param partialReward Complete with how much of the reward.
    struct DisputeInfo {
        uint256 taskId;
        uint96[] partialNativeReward;
        uint88[] partialReward;
    }

    /// @notice The governance plugin where the proposal to accept the dispute can be tracked.
    function getGovernancePlugin(IDAO _dao) external view returns (address);

    /// @notice The minimum amount of native currency that has to be attached to create a dispute.
    function getDisputeCost(IDAO _dao) external view returns (uint256);

    /// @notice Updates the governance plugin. The sender should be the DAO that wants to update its governance plugin.
    /// @param _governancePlugin The new governancePlugin.
    function updateGovernancePlugin(address _governancePlugin) external;

    /// @notice Updates the dispute cost. The sender should be the DAO that wants to update its dispute cost.
    /// @param _disputeCost The new dispute cost.
    function updateDisputeCost(uint256 _disputeCost) external;

    /// @notice Updates the manager and role. The sender should be the DAO that wants to update its manager and role
    /// @param _manager The new manager.
    /// @param _role The new role.
    function updateManager(IDAOManager _manager, uint256 _role) external;

    /// @notice Create a dispute for a task
    /// @param _dao The dao requested for the dispute.
    /// @param _metadata Metadata of the proposal.
    /// @param _startDate Start date of the proposal.
    /// @param _endDate End date of the proposal.
    /// @param _disputeInfo The proposed dispute.
    function createDispute(
        IDAO _dao,
        bytes calldata _metadata,
        uint64 _startDate,
        uint64 _endDate,
        DisputeInfo calldata _disputeInfo
    ) external payable;
}

src/TaskDisputes/TaskDisputes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {ERC165} from "../../lib/openzeppelin-contracts/contracts/utils/introspection/ERC165.sol";
import {OpenmeshENSReverseClaimable} from "../../lib/openmesh-admin/src/OpenmeshENSReverseClaimable.sol";

import {ITaskDisputes, ITasks, IDAOManager, IDAO} from "./ITaskDisputes.sol";

contract TaskDisputes is ERC165, OpenmeshENSReverseClaimable, ITaskDisputes {
    mapping(IDAO dao => DaoInfo info) private daoInfo;
    ITasks private immutable tasks;

    constructor(ITasks _tasks) {
        tasks = _tasks;
    }

    /// @inheritdoc ERC165
    function supportsInterface(bytes4 _interfaceId) public view virtual override returns (bool) {
        return _interfaceId == type(ITaskDisputes).interfaceId || super.supportsInterface(_interfaceId);
    }

    /// @inheritdoc ITaskDisputes
    function getGovernancePlugin(IDAO _dao) external view returns (address) {
        return daoInfo[_dao].governancePlugin;
    }

    /// @inheritdoc ITaskDisputes
    function getDisputeCost(IDAO _dao) external view returns (uint256) {
        return daoInfo[_dao].disputeCost;
    }

    /// @inheritdoc ITaskDisputes
    function updateGovernancePlugin(address _governancePlugin) external {
        daoInfo[IDAO(msg.sender)].governancePlugin = _governancePlugin;
    }

    /// @inheritdoc ITaskDisputes
    function updateDisputeCost(uint256 _disputeCost) external {
        daoInfo[IDAO(msg.sender)].disputeCost = _disputeCost;
    }

    /// @inheritdoc ITaskDisputes
    function updateManager(IDAOManager _manager, uint256 _role) external {
        DaoInfo storage info = daoInfo[IDAO(msg.sender)];
        info.manager = _manager;
        info.role = _role;
    }

    /// @inheritdoc ITaskDisputes
    function createDispute(
        IDAO _dao,
        bytes calldata _metadata,
        uint64 _startDate,
        uint64 _endDate,
        DisputeInfo calldata _disputeInfo
    ) external payable {
        DaoInfo memory info = daoInfo[_dao];

        // Dispute cost is required to make a dispute proposal. It is sent to the DAO.
        if (msg.value < info.disputeCost) {
            revert Underpaying();
        }

        // Normal address.transfer does not work with gas estimation
        (bool succes,) = address(_dao).call{value: msg.value}("");
        if (!succes) {
            revert TransferToDAOFailed();
        }

        IDAO.Action[] memory disputeActions = new IDAO.Action[](1);
        {
            bytes memory callData = abi.encodeWithSelector(
                tasks.completeByDispute.selector,
                _disputeInfo.taskId,
                _disputeInfo.partialNativeReward,
                _disputeInfo.partialReward
            );
            disputeActions[0] = IDAO.Action(address(tasks), 0, callData);
        }

        // This only works for DAOs governed with Aragons MajorityVoting
        IDAO.Action[] memory createProposalActions = new IDAO.Action[](1);
        {
            bytes memory callData = abi.encodeWithSignature(
                "createProposal(bytes,(address,uint256,bytes)[],uint256,uint64,uint64,VoteOption,bool)",
                _metadata,
                disputeActions,
                0, // failureMap
                _startDate,
                _endDate,
                0, // voteOption
                false // tryEarlyExecution
            );
            createProposalActions[0] = IDAO.Action(info.governancePlugin, 0, callData);
        }

        (bytes[] memory returnValues,) = info.manager.asDAO(_dao, info.role, createProposalActions, 0);
        (uint256 proposalId) = abi.decode(returnValues[0], (uint256));
        emit DisputeCreated(_dao, _disputeInfo, info.governancePlugin, proposalId);
    }
}
